pragma solidity ^0.5.0;

import "../node_modules/openzeppelin-solidity/contracts/crowdsale/Crowdsale.sol";
import "../node_modules/openzeppelin-solidity/contracts/crowdsale/emission/MintedCrowdsale.sol";
import "../node_modules/openzeppelin-solidity/contracts/crowdsale/validation/CappedCrowdsale.sol";

contract TokenCrowdsale is Crowdsale, MintedCrowdsale, CappedCrowdsale {

    uint256 public investorMinCap =  50000000000000000000;
    uint256 public investorHardCap = 500000000000000000000;

    mapping(address => uint256) public contributions;

    constructor(uint256 _rate, address payable _wallet, ERC20 _token, uint256 _cap)
	Crowdsale(_rate, _wallet, _token)
	CappedCrowdsale(_cap)
	public {
    }
}
